mapper.select:
select
id, subject_name, subject_difficult, settle_name, subject_type, subject_source, subject_parse, create_by,
create_time, update_by, update_time, is_deleted
from subject_info


        and id = #{id}


        and subject_name = #{subjectName}


        and subject_difficult = #{subjectDifficult}


        and settle_name = #{settleName}


        and subject_type = #{subjectType}


        and subject_source = #{subjectSource}


        and subject_parse = #{subjectParse}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from subject_info


        and id = #{id}


        and subject_name = #{subjectName}


        and subject_difficult = #{subjectDifficult}


        and settle_name = #{settleName}


        and subject_type = #{subjectType}


        and subject_source = #{subjectSource}


        and subject_parse = #{subjectParse}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}

select count(*)
from subject_info a,
subject_mapping b
where
a.id = b.subject_id
and b.category_id = #{categoryId}
and b.label_id = #{labelId}
and a.is_deleted = 0
and b.is_deleted = 0

    and a.subject_difficult = #{subjectInfo.subjectDifficult}


    and a.subject_type = #{subjectInfo.subjectType}

select a.id, a.subject_name, a.subject_difficult, a.settle_name, a.subject_type, a.subject_source, a.subject_parse, a.create_by, a.create_time, a.update_by, a.update_time, a.is_deleted from subject_info a, subject_mapping b where a.id = b.subject_id and b.category_id = #{categoryId} and b.label_id = #{labelId} and a.is_deleted = 0 and b.is_deleted = 0
and a.subject_difficult = #{subjectInfo.subjectDifficult}
and a.subject_type = #{subjectInfo.subjectType}
limit #{start},#{pageSize}
