mapper.select:
select
id,label_name,sort_num,create_by,create_time,update_by,update_time,is_deleted
from subject_label


        and id = #{id}


        and label_name = #{labelName}


        and sort_num = #{sortNum}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}

select count(1)
from subject_label


        and id = #{id}


        and label_name = #{labelName}


        and sort_num = #{sortNum}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}

SELECT id, label_name, sort_num FROM subject_label WHERE id IN
#{labelId}
AND is_deleted = 0
